import 'package:get/get.dart';

class CambioImagenController extends GetxController {
  @override
  void onInit() {
    super.onInit();
    cambioImg();
  }

  int indexUrlImg = 0;
  List<String> lista = [
    'https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcT6sR9Y0tMawRr2uXTmMBu8xitIDw1Thab5Sv3d2ZYboYNEMjQw',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5hxPzkIHkIdlYgo-ODwzgEtDyu0_BPOY-1dYjSwzDiuwuFr0u5wTreUy4BC4wqrYdXbA&usqp=CAU',
    'data:image/jpeg;base64,',
    'https://quizizz.com/media/resource/gs/quizizz-media/quizzes/6211aeba-3ae2-4fa2-8b91-eaf8a76703f2',
  ];
  String urlImg = '';

  void cambioImg() {
    urlImg = lista.first;
    update();
  }

  void cambioImgRL(int valor) {
    if (valor == 1) {
      if (indexUrlImg == 3) {
        indexUrlImg = 0;
      } else {
        indexUrlImg++;
      }
      urlImg = lista[indexUrlImg];
    } else {
      if (indexUrlImg == 0) {
        indexUrlImg = 3;
      } else {
        indexUrlImg--;
      }
      urlImg = lista[indexUrlImg];
    }
    update();
  }
}
